import React from 'react'
import {Link} from 'react-router-dom'
const posts=[
  {
    id:1,
    title:'This is my first post',
    desc:'This is the body of my first post',
    img:"https://unsplash.com/photos/a-man-standing-in-front-of-an-orange-and-yellow-background-trkREKD5Njg"
  },
  {
    id:2,
    title:'This is my second post',
    desc:'This is the body of my second',
    img:"https://unsplash.com/photos/a-couple-of-boats-sitting-on-top-of-a-sandy-beach-NbUi3PenY84"
  },
  {
    id:3,
    title:'This is my third post',
    desc:'This is the body of my third post',
    img:"https://unsplash.com/photos/a-close-up-of-two-people-holding-hands-9FMLzTr4p-o"
  },
  {
    id:4,
    title:'This is my fourth post',
    desc:'This is the body of my fourth post',
    img:"https://unsplash.com/photos/a-woman-standing-with-her-arms-crossed-in-front-of-a-building-plUAJDdSXyw"
  }
]

const Home=() =>{
  return (
    <div className='home'>
      <div className="posts">
        {posts.map(post =>(
          <div className='post' key={post.id}>
            <div className='img'>
            <img src={post.img} alt=""/>
          </div>
          <div className='content'>
            <Link to ={`post/${post.id}`}>
            <h1>{post.title}</h1>
            <p>{post.desc}</p>
            <button>Read More</button>
            </Link>
            </div>
          </div>
        ))}
      </div>
      </div>
  );
}

export default Home;
